import base64

from tests import client


def test_fileinfo(content_str_bytes=None):
    """把加密过的 json 数据文本，转换回可读的 json 数据。"""

    csb = (
        content_str_bytes
        or ""
    )

    return eval(base64.b64decode(csb).decode("utf-8"))


if __name__ == "__main__":
    print(test_fileinfo())
